package com.study;

public class IfElseIfBlock {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int x=10;
		//int x=30;
		int y=20;
		
		if(x<y)
		{
			System.out.println("x is greater than y");
		}else if(x==y)
		{
			System.out.println("x is similar to y");
	
		}else {
			System.out.println("Invalid");
		}
		
	}

}
