package com.study;

public class NestedIfElseBlock1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num = 12;

		if (num >= 10) {

			if (num <= 10) {

				System.out.println("Number is similar ");

			}else {
				System.out.println("Number is different");
			}

		}else {
			System.out.println("Number is greater than 12");
		}
	}

}
